CREATE TABLE "event" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"symbol" VARCHAR(255) NOT NULL,
	"name" VARCHAR(100),
	"interval" VARCHAR(10),
	"starttime" DATETIME NOT NULL,
	"volume" NUMERIC NOT NULL,
	"quotevolume" NUMERIC NOT NULL,
	"open" NUMERIC NOT NULL,
	"close" NUMERIC NOT NULL,
	"highest" NUMERIC,
	"lowest" NUMERIC,
	PRIMARY KEY("id")
);



CREATE TABLE "news" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"doamin" VARCHAR(255) NOT NULL,
	"title" VARCHAR(100),
	"url" VARCHAR(10),
	"published" DATETIME NOT NULL,
	"author" VARCHAR(255) NOT NULL,
	"source" VARCHAR(60) NOT NULL,
	"tag" VARCHAR(20) NOT NULL,
	PRIMARY KEY("id")
);



CREATE TABLE "technical" (
	"symbol" VARCHAR(255) NOT NULL,
	"sma20" NUMERIC NOT NULL UNIQUE,
	"ema12" NUMERIC NOT NULL,
	"rsi10" NUMERIC NOT NULL,
	"macd" NUMERIC NOT NULL,
	"bb" NUMERIC NOT NULL,
	"atr" NUMERIC NOT NULL,
	"va_high " NUMERIC NOT NULL,
	"va_low " NUMERIC NOT NULL,
	PRIMARY KEY("symbol")
);




CREATE TABLE "config" (
	"time" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"event_id" INTEGER,
	PRIMARY KEY("time")
);




CREATE TABLE "sentiment" (
	"id" INTEGER NOT NULL UNIQUE GENERATED BY DEFAULT AS IDENTITY,
	"event_id" INTEGER,
	"RSI_sen" VARCHAR(255),
	"MACD_sen" VARCHAR(255),
	"EMA_sen" VARCHAR(255),
	"bb_sen" VARCHAR(255),
	"SMA_sen" VARCHAR(255),
	"ATR_sen" VARCHAR(255),
	PRIMARY KEY("id")
);


